import React, { useEffect, useRef, useState } from 'react';

function ArchivioCard({ url, desc, aspect = 'square' }) {
  const imageContainerRef = useRef();

  useEffect(() => {
    let newImageHeight = imageContainerRef.current.innerWidth / 16 * 9;
    imageContainerRef.current.innerHeight = newImageHeight;
  }, [aspect]);

  return (
    <div className="flex flex-col gap-2 justify-start items-start">
      <div ref={imageContainerRef} className={`w-full aspect-${aspect}`}>
        <img src={url} className={`w-full h-full object-cover`} alt={desc} />
      </div>
      <h3 className='font-ibm-mono w-full text-start uppercase text-xs'>{desc}</h3>
    </div>
  );
}

export default ArchivioCard;
