import React, { useEffect, useRef, useState } from 'react';

function ArchivioCard({ url, desc, aspect = 'square' }) {

  const [imageHeight, setImageHeight] = useState();
  const imageContainerRef = useRef();

  useEffect(() => {
    let newImageHeight;
    if (aspect === 'square') {
       newImageHeight = imageContainerRef.current.clientWidth;
    } else {
      newImageHeight = imageContainerRef.current.clientWidth / 16 * 9;
    }
    setImageHeight(newImageHeight);
  }, [aspect]);

  return (
    <div className="flex flex-col gap-2 justify-start items-start">
      <div ref={imageContainerRef} className={`w-full ${aspect === 'square' ? 'aspect-square' : ''}`}>
        <img 
          src={url} 
          style={{ height: imageHeight }} 
          className={`w-full object-cover`} 
          alt={desc} 
        />
      </div>
      <h3 className='font-ibm-mono w-full text-start uppercase text-xs'>{desc}</h3>
    </div>
  );
}

export default ArchivioCard;
