import React from 'react';
import { useParams } from 'react-router-dom';
import { workItems } from '../';

function ProjectDetail() {
  const { id } = useParams();
  const project = workItems.find(item => item.id === parseInt(id));

  if (!project) {
    return <div>Project not found</div>;
  }

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold">{project.title}</h1>
      <p className="text-md text-gray-700">{project.year}</p>
      <img src={project.url} alt={project.title} className="object-cover h-96 w-full rounded-md my-4" />
      <p className="text-md">{project.description}</p>
    </div>
  );
}

export default ProjectDetail;
